mapper.select:
select 

from sign
where u_id = #{uId,jdbcType=VARCHAR}
  and groupName = #{groupname,jdbcType=VARCHAR}
  and clazzName = #{clazzname,jdbcType=VARCHAR}
  and signDate = #{signdate,jdbcType=DATE}

mapper.update:
update sign


    state = #{state,jdbcType=INTEGER},


where u_id = #{uId,jdbcType=VARCHAR}
  and groupName = #{groupname,jdbcType=VARCHAR}
  and clazzName = #{clazzname,jdbcType=VARCHAR}
  and signDate = #{signdate,jdbcType=DATE}

update sign
set state = #{state,jdbcType=INTEGER}
where u_id = #{uId,jdbcType=VARCHAR}
  and groupName = #{groupname,jdbcType=VARCHAR}
  and clazzName = #{clazzname,jdbcType=VARCHAR}
  and signDate = #{signdate,jdbcType=DATE}
